import React, { useState } from 'react';
import axios from 'axios'; // Added Axios import
import {  } from 'react-router-dom';

const CreateTopic = ({ onClose, onCancel }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      // Axios POST request instead of fetch
      const response = await axios.post(
        'https://localhost:5000/api/roots',
        { title, content },
        {
          headers: { 'Content-Type': 'application/json' }
        }
      );

      // Axios automatically parses JSON - no need for response.json()
      const data = response.data;

      // Axios throws errors for non-2xx responses automatically
      // so we don't need manual status code checking

      // Notify parent component about successful creation
      onClose();
    } catch (error) {
      console.error('Error creating topic:', error);

      // Enhanced error handling for Axios
      setError(
        error.response?.data?.message || 
        error.message || 
        'An error occurred. Please try again.'
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="topic-form">
      <h2>Create New Topic</h2>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter topic title"
            required
            disabled={isSubmitting}
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label>Content</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Describe your topic..."
            rows={5}
            required
            disabled={isSubmitting}
            className="form-control"
          />
        </div>

        <div className="form-actions">
          <button 
            type="submit" 
            disabled={isSubmitting}
            className="btn btn-primary"
          >
            {isSubmitting ? (
              <>
                <span className="spinner-border spinner-border-sm" role="status"></span>
                Creating...
              </>
            ) : 'Create Topic'}
          </button>

          <button 
            type="button" 
            className="btn btn-secondary" 
            onClick={onCancel}
            disabled={isSubmitting}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateTopic;
