import React, { useEffect, useRef, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';
import Dashboard from '../components/Dashboard';
import SearchBar from '../components/SearchBar'; // Use the new search bar
import SearchResults from '../components/SearchResults'; // Add this new component
import '../css/App.css';
import '../css/SearchBar.css'; // Add this CSS import

const Home = () => {
  const { id } = useParams();
  const [roots, setRoots] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [dashboardOpen, setDashboardOpen] = useState(false);
  const [sortOption, setSortOption] = useState('POPULARITY_DESC');
  const [searchResults, setSearchResults] = useState([]); // New state for search results
  const [isSearching, setIsSearching] = useState(false); // Search loading state

  const dashboardTimer = useRef(null);

  const handleHoverEnter = () => {
    clearTimeout(dashboardTimer.current);
    setDashboardOpen(true);
  };

  const handleHoverLeave = () => {
    dashboardTimer.current = setTimeout(() => {
      setDashboardOpen(false);
    }, 300);
  };

  const handleDashboardEnter = () => {
    clearTimeout(dashboardTimer.current);
  };

  const handleDashboardLeave = () => {
    dashboardTimer.current = setTimeout(() => {
      setDashboardOpen(false);
    }, 300);
  };

  const fetchRoots = React.useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/roots/${id}`);
      const data = await response.json();
      const processedData = data.map(root => ({
        ...root,
        created_at: root.created_at || new Date().toISOString()
      }));
      setRoots(Array.isArray(processedData) ? processedData : []);
    } catch (error) {
      console.error('Error fetching root nodes:', error);
      setRoots([]);
    } finally {
      setLoading(false);
    }
  }, [id]);

  React.useEffect(() => {
    fetchRoots();
  }, [fetchRoots]);

  const handleNewRootCreated = () => {
    setShowCreateForm(false);
    fetchRoots();
  };

  // NEW SEARCH HANDLER
  const handleSearch = async (query) => {
    if (!query.trim()) {
      setSearchResults([]);
      return;
    }

    setIsSearching(true);
    try {
      const response = await fetch('http://localhost:5000/api/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query })
      });
      const results = await response.json();
      setSearchResults(results);
    } catch (error) {
      console.error('Search failed:', error);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  // Apply sorting based on the selected option
  const sortedRoots = React.useMemo(() => {
    if (!roots.length) return [];

    return [...roots].sort((a, b) => {
      switch (sortOption) {
        case 'POPULARITY_DESC':
          return b.popularity - a.popularity;
        case 'POPULARITY_ASC':
          return a.popularity - b.popularity;
        case 'DATE_DESC':
          return new Date(b.created_at) - new Date(a.created_at);
        case 'DATE_ASC':
          return new Date(a.created_at) - new Date(b.created_at);
        default:
          return 0;
      }
    });
  }, [roots, sortOption]);

  if (loading) return (
    <div className="page-container">
      <div className="hamburger-icon" onMouseEnter={handleHoverEnter}>
        ☰
      </div>
      {dashboardOpen && (
        <div 
          className="dashboard-panel"
          onMouseEnter={handleDashboardEnter}
          onMouseLeave={handleDashboardLeave}
        >
          <Dashboard />
        </div>
      )}
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading root nodes...</p>
      </div>
    </div>
  );

  return (
    <div className="page-container">
      <div 
        className="hamburger-icon" 
        onMouseEnter={handleHoverEnter}
        onMouseLeave={handleHoverLeave}
      >
        ☰
      </div>

      {dashboardOpen && (
        <div 
          className="dashboard-panel"
          onMouseEnter={handleDashboardEnter}
          onMouseLeave={handleDashboardLeave}
        >
          <Dashboard />
        </div>
      )}

      <main className="roots-container">
        <div className="roots-header">
          <h1>Knowledge Tree</h1>
          <p>Explore topics and build your knowledge graph</p>
        </div>

        <div className="controls">
          {/* Updated SearchBar with submit functionality */}
          <SearchBar onSearch={handleSearch} />

          {/* Display search results */}
          {searchResults.length > 0 || isSearching ? (
            <SearchResults 
              results={searchResults} 
              isLoading={isSearching} 
            />
          ) : null}

          {/* Sorting Controls */}
          <div className="sorting-controls">
            <span>Sort by: </span>
            <select
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
              className="sort-select"
            >
              <option value="POPULARITY_DESC">Popularity (High to Low)</option>
              <option value="POPULARITY_ASC">Popularity (Low to High)</option>
              <option value="DATE_DESC">Newest First</option>
              <option value="DATE_ASC">Oldest First</option>
            </select>
          </div>

          <button 
            className="btn btn-primary" 
            onClick={() => setShowCreateForm(true)}
          >
            + New Root Topic
          </button>
        </div>

        {showCreateForm && (
          <div className="modal-overlay">
            <div className="modal-content">
              <CreateTopic 
                onClose={handleNewRootCreated} 
                onCancel={() => setShowCreateForm(false)} 
              />
            </div>
          </div>
        )}

        {/* Only show root nodes when not showing search results */}
        {searchResults.length === 0 && !isSearching && (
          <section className="roots-grid">
            {sortedRoots.length > 0 ? (
              sortedRoots.map(root => (
                <div key={root.id} className="root-card">
                  <Link to={`/node/${root.id}`} className="root-link">
                    <div className="root-icon">
                      <span>📚</span>
                    </div>
                    <div className="root-info">
                      <h3 className="root-title">{root.title}</h3>
                      <div className="root-meta">
                        <span className="sub-topics">Leafs: {root.leaf_count_in_subtree || 0}</span>
                        <span className="popularity">Popularity: {root.popularity}</span>
                        <span className="date">
                          {new Date(root.created_at).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  </Link>
                </div>
              ))
            ) : (
              <div className="empty-state">
                <div className="empty-icon">🌿</div>
                <h3>No root nodes found</h3>
                <p>Be the first to create a root topic</p>
              </div>
            )}
          </section>
        )}
      </main>
    </div>
  );
};

export default Home;
