// src/pages/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';
import DashboardComponent from '../CodeDashboard/components/DashboardComponent';
import SearchBar from './SearchBar';
import useDashboardHover from '../CodeDashboard/hooks/UseDashboardHover';
import '../css/App.css';

const HomePage = () => {
  const [searchTerm, setSearchTerm] = React.useState('');
  const [results, setResults] = React.useState([]);
  const [loading, setLoading] = React.useState(false);

  // Dashboard hover hook
  const {
    dashboardOpen,
    handleHoverEnter,
    handleHoverLeave,
    handleDashboardEnter,
    handleDashboardLeave
  } = useDashboardHover();

  const handleSearch = async () => {

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/search/${searchTerm}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      });

      const data = await response.json();
      setResults(data);
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page-container">
      <DashboardComponent 
        dashboardOpen={dashboardOpen}
        handleHoverEnter={handleHoverEnter}
        handleHoverLeave={handleHoverLeave}
        handleDashboardEnter={handleDashboardEnter}
        handleDashboardLeave={handleDashboardLeave}
      />

      <main className="search-page">
        <div className="search-header">
          <h1>Knowledge Explorer</h1>
          <p>Discover and navigate through our knowledge repository</p>
        </div>

        <SearchBar 
          onSearch={handleSearch}
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
        />

        {loading && (
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Searching knowledge base...</p>
          </div>
        )}

        <section className="results-section">
          {results.length > 0 ? (
            <div className="results-container">
              <h2>Search Results ({results.length})</h2>
              <div className="results-grid">
                {results.map(result => (
                  <div key={result.id} className="result-card">
                    <Link to={`/node/${result.id}`} className="result-link">
                      <div className="result-icon">📚</div>
                      <div className="result-content">
                        <h3 className="result-title">{result.title}</h3>
                        <div className="result-meta">
                          <span>Leaves: {result.leafCount || 0}</span>
                          <span>Popularity: {result.popularity}</span>
                          <span>Score: {result.score || "N/A"}</span>
                        </div>
                      </div>
                    </Link>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            !loading && searchTerm && (
              <div className="empty-results">
                <p>No results found for "{searchTerm}"</p>
                <button 
                  className="btn btn-secondary"
                  onClick={() => setSearchTerm('')}
                >
                  Clear Search
                </button>
              </div>
            )
          )}
        </section>
      </main>
    </div>
  );
};

export default HomePage;
