import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const CreateChild = () => {
  const { id } = useParams();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form behavior
    setIsSubmitting(true);

    try {
      // Create both requests without awaiting individually
      const promise1 = fetch('http://localhost:5000/api/nodes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          parent_id: parseInt(id), scores: 1,
          title, content, statementTrueFalseFlag: 1
        })
      });

      const promise2 = fetch('http://localhost:5000/api/nodes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          parent_id: parseInt(id), scores: 1,
          title, content, statementTrueFalseFlag: 0
        })
      });

      // Await both concurrently
      const [res1, res2] = await Promise.all([promise1, promise2]);

      // Check both responses
      if (!res1.ok || !res2.ok) throw new Error('API call failed');

      navigate(`/node/${id}`); // Redirect AFTER both succeed

      } catch (error) {
        console.error('Error:', error);
      } finally {
        setIsSubmitting(false);
      }
  };

  return (
    <div className="topic-form">
      <h2>Create New Child</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter True/False Statement title"
            required
            disabled={isSubmitting}
          />
        </div>
        <div>
          <label>Content</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Describe your True/False Statement..."
            rows={5}
            required
            disabled={isSubmitting}
          />
        </div>
        <button 
          type="submit" 
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Creating...' : 'Create True/False Statement'}
        </button>
        <button className="btn btn-secondary" onClick={() => navigate(`/node/${id}`)}>
            Cancel
       </button>
      </form>
    </div>
  );
};

export default CreateChild;
