// services/themeService.js
import axios from 'axios';

export const updateThemePreference = async (isDarkMode) => {
  const token = localStorage.getItem('token');
  const themeFlag = isDarkMode ? 0 : 1; // Map: dark=true → 0, light=false → 1
  await axios.post('/api/theme', { themeFlag }, { 
    headers: { Authorization: `Bearer ${token}` } 
  });
};

export const fetchUserTheme = async () => {
  // Fix: Add credentials and use correct response field
  const response = await axios.get('/api/theme', { 
    withCredentials: true 
  });

  // Fix: Use darkMode field from response
  return response.data.darkMode; // true = dark, false = light
};
