import React from 'react';
import { Link } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';
import Dashboard from '../components/Dashboard';
import '../css/App.css';

const RootsPage = () => {
  const [roots, setRoots] = React.useState([]);
  const [trending, setTrending] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [showCreateForm, setShowCreateForm] = React.useState(false);
  const [dashboardOpen, setDashboardOpen] = React.useState(false);
  const [searchTerm, setSearchTerm] = React.useState('');
  const [sortOption, setSortOption] = React.useState('popularity');

  // Dashboard hover handling
  const dashboardTimer = React.useRef(null);

  const handleHoverEnter = () => {
    clearTimeout(dashboardTimer.current);
    setDashboardOpen(true);
  };

  const handleHoverLeave = () => {
    dashboardTimer.current = setTimeout(() => {
      setDashboardOpen(false);
    }, 300);
  };

  const handleDashboardEnter = () => {
    clearTimeout(dashboardTimer.current);
  };

  const handleDashboardLeave = () => {
    dashboardTimer.current = setTimeout(() => {
      setDashboardOpen(false);
    }, 300);
  };

  const fetchRoots = React.useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/roots?search=${searchTerm}&sort=${sortOption}`);
      const data = await response.json();
      setRoots(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching root nodes:', error);
      setRoots([]);
    } finally {
      setLoading(false);
    }
  }, [searchTerm, sortOption]);

  const fetchTrending = React.useCallback(async () => {
    try {
      const response = await fetch('http://localhost:5000/api/trending');
      const data = await response.json();
      setTrending(Array.isArray(data) ? data.slice(0, 5) : []);
    } catch (error) {
      console.error('Error fetching trending topics:', error);
      setTrending([]);
    }
  }, []);

  React.useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      await fetchTrending();
      await fetchRoots();
    };

    fetchData();
  }, [fetchRoots, fetchTrending]);

  const handleNewRootCreated = () => {
    setShowCreateForm(false);
    fetchRoots();
    fetchTrending();
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const sortOptions = [
    { value: 'popularity', label: 'Most Popular' },
    { value: 'newest', label: 'Newest' },
    { value: 'oldest', label: 'Oldest' },
    { value: 'a-z', label: 'A to Z' },
  ];

  if (loading) return (
    <div className="page-container">
      <div className="hamburger-icon" onMouseEnter={handleHoverEnter}>
        ☰
      </div>
      {dashboardOpen && (
        <div 
          className="dashboard-panel"
          onMouseEnter={handleDashboardEnter}
          onMouseLeave={handleDashboardLeave}
        >
          <Dashboard />
        </div>
      )}
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading content...</p>
      </div>
    </div>
  );

  return (
    <div className="page-container">
      {/* Hamburger Icon */}
      <div 
        className="hamburger-icon" 
        onMouseEnter={handleHoverEnter}
        onMouseLeave={handleHoverLeave}
      >
        ☰
      </div>

      {/* Dashboard Panel */}
      {dashboardOpen && (
        <div 
          className="dashboard-panel"
          onMouseEnter={handleDashboardEnter}
          onMouseLeave={handleDashboardLeave}
        >
          <Dashboard />
        </div>
      )}

      <main className="home-container">
        <div className="home-header">
          <h1>Knowledge Tree</h1>
          <p>Explore topics and build your knowledge graph</p>
        </div>

        {/* Search & Sort Controls */}
        <div className="controls-bar">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search topics..."
              value={searchTerm}
              onChange={handleSearch}
              className="search-input"
            />
            <span className="search-icon">🔍</span>
          </div>

          <div className="sort-filter">
            <label htmlFor="sort-select">Sort by:</label>
            <select 
              id="sort-select" 
              value={sortOption} 
              onChange={handleSortChange}
              className="sort-select"
            >
              {sortOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <button 
            className="btn btn-primary" 
            onClick={() => setShowCreateForm(true)}
          >
            + New Topic
          </button>
        </div>

        {/*
        Trending Section 
        <section className="trending-section">
          <h2 className="section-title">🔥 Trending Topics</h2>
          <div className="trending-grid">
            {trending.length > 0 ? (
              trending.map(topic => (
                <div key={topic.id} className="trending-card">
                  <Link to={`/node/${topic.id}`} className="trending-link">
                    <span className="trending-badge">🔥</span>
                    <h3 className="trending-title">{topic.title}</h3>
                    <div className="trending-meta">
                      <span>{topic.popularity} views</span>
                    </div>
                  </Link>
                </div>
              ))
            ) : (
              <div className="empty-state">
                <div className="empty-icon">🌱</div>
                <p>No trending topics found</p>
              </div>
            )}
          </div>
        </section>
        */}

        {/* All Topics Section */}
        <section className="all-topics-section">
          <div className="section-header">
            <h2 className="section-title">All Topics</h2>
              <section className="roots-grid">
          {roots.length > 0 ? (
            roots.map(root => (
              <div key={root.id} className="root-card">
                <Link to={`/node/${root.id}`} className="root-link">
                  <div className="root-icon">
                    <span>📚</span>
                  </div>
                  <div className="root-info">
                    <h3 className="root-title">{root.title}</h3>
                    <div className="root-meta">
                      <span className="sub-topics">Leafs: {root.leaf_count_in_subtree || 0}</span>
                      <span className="popularity">Popularity: {root.popularity}</span>
                      <span className="sub-topics">Score: {root.score || "N/A"}</span>
                    </div>
                  </div>
                </Link>
              </div>
            ))
          ) : (
            <div className="empty-state">
              <div className="empty-icon">🌿</div>
              <h3>No root nodes found</h3>
              <p>Be the first to create a root topic</p>
            </div>
          )}
        </section>            
          </div>

          {showCreateForm && (
            <div className="modal-overlay">
              <div className="modal-content">
                <CreateTopic 
                  onClose={handleNewRootCreated} 
                  onCancel={() => setShowCreateForm(false)} 
                />
              </div>
            </div>
          )}

          <div className="roots-grid">
            {roots.length > 0 ? (
              roots.map(root => (
                <div key={root.id} className="root-card">
                  <Link to={`/node/${root.id}`} className="root-link">
                    <div className="root-icon">
                      <span>📚</span>
                    </div>
                    <div className="root-info">
                      <h3 className="root-title">{root.title}</h3>
                      <div className="root-meta">
                        <span className="sub-topics">Leafs: {root.leaf_count_in_subtree || 0}</span>
                        <span className="popularity">Views: {root.popularity}</span>
                      </div>
                    </div>
                  </Link>
                </div>
              ))
            ) : (
              <div className="empty-state">
                <div className="empty-icon">🌿</div>
                <h3>No topics found</h3>
                <p>Create a new topic to get started</p>
              </div>
            )}
          </div>
        </section>
      </main>
    </div>
  );
};

export default RootsPage;
