// NodePage.js
import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import NodeDetails from '../components/NodePageComponents/NodeDetails';
import CommentsSection from '../components/NodePageComponents/CommentsSection';
import ChildrenList from '../components/NodePageComponents/ChildrenList';
import ActionButtons from '../components/NodePageComponents/ActionButtons';
import Breadcrumb from '../components/NodePageComponents/Breadcrumb';
import TreeVisualization from '../components/NodePageComponents/TreeVisualization';
import '../css/NodePage.css';

const NodePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [node, setNode] = React.useState(null);
  const [children, setChildren] = React.useState([]);
  const [path, setPath] = React.useState([]);
  const [treeData, setTreeData] = React.useState(null);
  const [isLoading, setIsLoading] = React.useState(true);

  // Fetch node data
  React.useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const [nodeRes, childrenRes, pathRes, treeRes] = await Promise.all([
          fetch(`http://localhost:5000/api/nodes/${id}`),
          fetch(`http://localhost:5000/api/nodes/${id}/children`),
          fetch(`http://localhost:5000/api/nodes/${id}/path`),
          fetch(`http://localhost:5000/api/nodes/${id}/tree?depth=4`)
        ]);

        setNode(await nodeRes.json());
        setChildren(await childrenRes.json());
        setPath((await pathRes.json()).reverse());
        setTreeData(await treeRes.json());
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (isLoading) return (
    <div className="flex justify-center my-16">
      <div className="flex flex-col items-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        <span className="mt-3 text-gray-600">Loading node details...</span>
      </div>
    </div>
  );

  if (!node) return (
    <div className="alert alert-error max-w-2xl mx-auto mt-8">
      <div className="flex-1">
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" 
             className="w-6 h-6 mx-2 stroke-current">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
        </svg>
        <label>Node not found or unavailable</label>
      </div>
    </div>
  );

  return (
    <div className="container py-6 max-w-6xl">
      <Breadcrumb path={path} />

      <div className="grid grid-cols-1 lg:grid-cols-5 gap-6 mt-6">
        <div className="lg:col-span-3 space-y-6">
          <NodeDetails node={node} />
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-4">
            <h3 className="font-semibold text-gray-800 mb-3">Children</h3>
            <ChildrenList children={children} />
          </div>
        </div>

        <div className="lg:col-span-2 space-y-6">
          <div className="sticky top-24">
            <div className="mt-6 bg-white rounded-xl shadow-sm border border-gray-200 p-4">
              <ActionButtons node={node} navigate={navigate} nodeId={id} />
            </div>
          </div>
        </div>
        <CommentsSection nodeId={id} />
            <TreeVisualization treeData={treeData} maxDepth={4} />
      </div>
    </div>
  );
};

export default NodePage;
