import React from 'react';
import { useParams, Link } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';
import DashboardComponent from '../../CodeDashboard/components/DashboardComponent';
import useDashboardHover from '../../CodeDashboard/hooks/UseDashboardHover';
import ScoreBar from '../../utils/visuals/ScoreBar';  // Standard import
import '../../css/App.css';

const RootsPage = () => {
  const { id } = useParams();
  const [roots, setRoots] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [showCreateForm, setShowCreateForm] = React.useState(false);

  // NEW: Add state for sort option
  const [sortOption, setSortOption] = React.useState('latest');

  const {
    dashboardOpen,
    handleHoverEnter,
    handleHoverLeave,
    handleDashboardEnter,
    handleDashboardLeave
  } = useDashboardHover();

  const fetchRoots = React.useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/roots/${id}`);
      const data = await response.json();
      setRoots(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching root nodes:', error);
      setRoots([]);
    } finally {
      setLoading(false);
    }
  }, [id]);

  React.useEffect(() => {
    fetchRoots();
  }, [fetchRoots]);

  const handleNewRootCreated = () => {
    setShowCreateForm(false);
    fetchRoots();
  };

  // NEW: Sort roots based on selected option
  const sortedRoots = React.useMemo(() => {
    // Create a copy to avoid mutating original state
    const toSort = [...roots];

    return toSort.sort((a, b) => {
      // Sort by date (assuming created_at exists in your data)
      if (sortOption === 'latest') {
        return new Date(b.created_at) - new Date(a.created_at);
      } else {
        return new Date(a.created_at) - new Date(b.created_at);
      }
    });
  }, [roots, sortOption]);

  if (loading) return (
    <div className="page-container">
      <DashboardComponent 
        dashboardOpen={dashboardOpen}
        handleHoverEnter={handleHoverEnter}
        handleHoverLeave={handleHoverLeave}
        handleDashboardEnter={handleDashboardEnter}
        handleDashboardLeave={handleDashboardLeave}
      />
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading root nodes...</p>
      </div>
    </div>
  );

  return (
    <div className="page-container">
      <DashboardComponent 
        dashboardOpen={dashboardOpen}
        handleHoverEnter={handleHoverEnter}
        handleHoverLeave={handleHoverLeave}
        handleDashboardEnter={handleDashboardEnter}
        handleDashboardLeave={handleDashboardLeave}
      />

      <main className="roots-container">
        <div className="roots-header">
          <h1>Knowledge Tree</h1>
          <p>Explore topics and build your knowledge graph</p>
        </div>

        <div className="controls">
          <button 
            className="btn btn-primary" 
            onClick={() => setShowCreateForm(true)}
          >
            + New Root Topic
          </button>

          {/* NEW: Filter controls */}
          <div className="sort-controls">
            <label>Sort by: </label>
            <select 
              value={sortOption} 
              onChange={(e) => setSortOption(e.target.value)}
              className="sort-select"
            >
              <option value="latest">Latest</option>
              <option value="oldest">Oldest</option>
            </select>
          </div>
        </div>

        {showCreateForm && (
          <div className="modal-overlay">
            <div className="modal-content">
              <CreateTopic 
                onClose={handleNewRootCreated} 
                onCancel={() => setShowCreateForm(false)} 
              />
            </div>
          </div>
        )}

        <section className="roots-grid">
          {sortedRoots.length > 0 ? (
            sortedRoots.map(root => (
              <div key={root.id} className="root-card">
                <Link to={`/node/${root.id}`} className="root-link">
                  <div className="root-icon">
                    <span>ðŸ“š</span>
                  </div>
                  <div className="root-info">
                    <h3 className="root-title">{root.title}</h3>
                    <div className="root-meta">
                      <span className="sub-topics">Leafs: {root.leaf_count_in_subtree || 0}</span>
                      <span className="popularity">Popularity: {root.popularity}</span>
                      <ScoreBar 
                        score={root.score * 100} 
                        height={10}
                        color="#4285F4"
                        label="Knowledge Score"
                      />
                      {/* NEW: Display date if available */}
                      {root.created_at && (
                        <span className="date">
                          {new Date(root.created_at).toLocaleDateString()}
                        </span>
                      )}
                    </div>
                  </div>
                </Link>
              </div>
            ))
          ) : (
            <div className="empty-state">
              <div className="empty-icon">ðŸŒ¿</div>
              <h3>No root nodes found</h3>
              <p>Be the first to create a root topic</p>
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default RootsPage;
