// src/pages/RootsPage.js
import React from 'react';
import { Link } from 'react-router-dom';

const RootsPage = () => {
  const [roots, setRoots] = React.useState([]);
  const [loading, setLoading] = React.useState(true);

  // Fetch root nodes
  React.useEffect(() => {
    const fetchRoots = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/nodes/roots');
        const data = await response.json();
        setRoots(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching root nodes:', error);
        setLoading(false);
      }
    };

    fetchRoots();
  }, []);

  if (loading) {
    return <div>Loading root nodes...</div>;
  }

  return (
    <div>
      <h1>Root Nodes</h1>
      <ul className="list-group">
        {roots.map(root => (
          <li key={root.id} className="list-group-item">
            <Link to={`/node/${root.id}`} className="btn btn-link">
              Node {root.id} (Popularity: {root.popularity})
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RootsPage;
