const CreateComment = () => {
  const { id } = useParams();
  const [content, setContent] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form behavior
    setIsSubmitting(true);

    try {
      // Create both requests without awaiting individually
      const response = fetch('http://localhost:5000/api/nodes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
            content
        })
      });

      // Await both concurrently
      const res1 = await Promise.all([response]);

      // Check both responses
      if (!res1.ok ) throw new Error('API call failed');

      navigate(`/node/${id}`); // Redirect AFTER both succeed

      } catch (error) {
        console.error('Error:', error);
      } finally {
        setIsSubmitting(false);
      }
  };

  return (
    <div className="topic-form">
      <h2>Create New Comment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
