import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";
import PrivateRoute from '../../components/LoginSignupComponents/PrivateRoute';
import { createComment } from '../../services/commentsAPI'; // New import

const CreateComment = ({ onCommentCreated }) => {
  const { id } = useParams();
  const [content, setContent] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      const createdComment = await createComment(id, content); // Use service function

      if (onCommentCreated) {
        onCommentCreated(createdComment);
      } else {
        navigate(`/node/${id}`);
      }
    } catch (error) {
      console.error('Submission error:', error);
      setError(error.message || 'Failed to create comment');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <PrivateRoute>
      {/* ... existing JSX remains unchanged ... */}
    </PrivateRoute>
  );
};

export default CreateComment;
