import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";
import PrivateRoute from '../../components/LoginSignupComponents/PrivateRoute';
import { createComment } from '../../services/commentsAPI'; // New import

const CreateComment = ({ onCommentCreated }) => {
  const { id } = useParams();
  const [content, setContent] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      const createdComment = await createComment(id, content); // Use service function

      if (onCommentCreated) {
        onCommentCreated(createdComment);
      } else {
        navigate(`/node/${id}`);
      }

      window.location.reload();
    } catch (error) {
      console.error('Submission error:', error);
      setError(error.message || 'Failed to create comment');
    }
    setIsSubmitting(false);
  };

   return (
    <PrivateRoute>
    <div className="topic-form p-4 border rounded shadow-sm bg-white">
      <h2 className="mb-3">Create New Comment</h2>

      {error && (
        <div className="alert alert-danger mb-3">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Comment</label>
          <textarea
            className="form-control"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Enter your comment"
            required
            disabled={isSubmitting}
            rows={4}
          />
        </div>

        <div className="d-flex justify-content-end gap-2">


          <button
            type="submit"
            className="btn btn-primary"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" aria-hidden="true"></span>
                Submitting...
              </>
            ) : 'Submit Comment'}
          </button>
        </div>
      </form>
    </div>
    </PrivateRoute>
  );
};

export default CreateComment;
