import React from 'react';
import { Link } from 'react-router-dom';
import ScoreBar from '../../utils/visuals/ScoreBar';
import '../../CodeCSS/App.css';

const ChildrenList = ({ children: childrenNodes }) => (
  <div className="root-card mb-4">
    <div className="card-header bg-secondary py-3">
      <h2 className="h5 mb-0">Children Nodes</h2>
    </div>
    <div className="list-group list-group-flush">
      {childrenNodes.length === 0 ? (
        <div className="list-group-item text-center py-3 text-muted">
          No child nodes
        </div>
      ) : (
        childrenNodes.map(child => (
          <Link 
            key={child.id} 
            to={`/node/${child.id}`}
            className="root-card list-group-item-action d-flex justify-content-between align-items-center p-3 children-node-link"
          >
            <span className={`fw-medium ${child.statementTrueFalseFlag ? 'true-color' : 'false-color'}`}>
              {child.title || `Node ${child.id}`} 
              <span className="text-muted">: {child.statementTrueFalseFlag ? 'True' : 'False'}</span> 
            </span>
            <ScoreBar 
              score={child.score} 
              height={12}
              showValueInside
              color="var(--accent-primary)"
              label="Weighted Average Score"
              isCompleted={child.allChildrenCompleted}
            />
          </Link>
        ))
      )}
    </div>
  </div>
);

export default ChildrenList;
