import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";
import PrivateRoute from '../components/LoginSignupComponents/PrivateRoute';


const CreateComment = ({ onCommentCreated }) => {
  const { id } = useParams();
  const [content, setContent] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      const response = await fetch(`http://localhost:5000/api/nodes/${id}/comments`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ content })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'API request failed');
      }

      const createdComment = await response.json();  // Parse response

      if (onCommentCreated) {
        onCommentCreated(createdComment);  // Pass actual comment data
      } else {
        navigate(`/node/${id}`);
      }

    } catch (error) {
      console.error('Submission error:', error);
      setError(error.message || 'Failed to create comment');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="topic-form p-4 border rounded shadow-sm bg-white">
      <h2 className="mb-3">Create New Comment</h2>

      {error && (
        <div className="alert alert-danger mb-3">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Comment</label>
          <textarea
            className="form-control"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Enter your comment"
            required
            disabled={isSubmitting}
            rows={4}
          />
        </div>

        <div className="d-flex justify-content-end gap-2">


          <button
            type="submit"
            className="btn btn-primary"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" aria-hidden="true"></span>
                Submitting...
              </>
            ) : 'Submit Comment'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateComment;
