import React from 'react';
import { Link } from 'react-router-dom';
import ScoreBar from '../../utils/visuals/ScoreBar'

const ChildrenList = ({ children: childrenNodes }) => (
  <div className="card mb-4">
    <div className="card-header bg-light py-3">
      <h2 className="h5 mb-0">Children Nodes</h2>
    </div>
    <div className="list-group list-group-flush">
      {childrenNodes.length === 0 ? (
        <div className="list-group-item text-center py-3 text-muted">
          No child nodes
        </div>
      ) : (
        childrenNodes.map(child => (
          <Link 
            key={child.id} 
            to={`/node/${child.id}`}
            className="list-group-item list-group-item-action d-flex justify-content-between align-items-center"
          >
            <span className={child.statementTrueFalseFlag ? 'true-color' : 'false-color'}>
              {child.title || `Node ${child.id}`} 
              {child.statementTrueFalseFlag ? 
                ": True" : 
                ": False"} 
            </span>
            <ScoreBar 
              score={(child.score*100).toFixed(2)} 
              height={10}
              color="#4285F4"
              label="Weighted Average Score"
            />
          </Link>
        ))
      )}
    </div>
  </div>
);

export default ChildrenList;
