import React, { useEffect, useRef, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';
import Dashboard from '../components/Dashboard';
import SearchBar from '../components/SearchBar';
import '../css/App.css';


const Home = () => {
  const { id } = useParams();
  const [roots, setRoots] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [dashboardOpen, setDashboardOpen] = useState(false);

  const dashboardTimer = useRef(null);

  const handleHoverEnter = () => {
    clearTimeout(dashboardTimer.current);
    setDashboardOpen(true);
  };

  const handleHoverLeave = () => {
    dashboardTimer.current = setTimeout(() => {
      setDashboardOpen(false);
    }, 300);
  };

  const handleDashboardEnter = () => {
    clearTimeout(dashboardTimer.current);
  };

  const handleDashboardLeave = () => {
    dashboardTimer.current = setTimeout(() => {
      setDashboardOpen(false);
    }, 300);
  };

  const fetchRoots = React.useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/roots/${id}`);
      const data = await response.json();
      setRoots(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching root nodes:', error);
      setRoots([]);
    } finally {
      setLoading(false);
    }
  }, [id]);

  React.useEffect(() => {
    fetchRoots();
  }, [fetchRoots]);

  const handleNewRootCreated = () => {
    setShowCreateForm(false);
    fetchRoots();
  };

  // Function to handle search selection
  const handleSearchSelect = (topic) => {
    // Navigate to the selected topic
    window.location.href = `/node/${topic.id}`;
  };

  if (loading) return (
    <div className="page-container">
      <div className="hamburger-icon" onMouseEnter={handleHoverEnter}>
        â˜°
      </div>
      {dashboardOpen && (
        <div 
          className="dashboard-panel"
          onMouseEnter={handleDashboardEnter}
          onMouseLeave={handleDashboardLeave}
        >
          <Dashboard />
        </div>
      )}
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading root nodes...</p>
      </div>
    </div>
  );

  return (
    <div className="page-container">
      {/* Hamburger Icon */}
      <div 
        className="hamburger-icon" 
        onMouseEnter={handleHoverEnter}
        onMouseLeave={handleHoverLeave}
      >
        â˜°
      </div>

      {/* Dashboard Panel */}
      {dashboardOpen && (
        <div 
          className="dashboard-panel"
          onMouseEnter={handleDashboardEnter}
          onMouseLeave={handleDashboardLeave}
        >
          <Dashboard />
        </div>
      )}

      <main className="roots-container">
        <div className="roots-header">
          <h1>Knowledge Tree</h1>
          <p>Explore topics and build your knowledge graph</p>
        </div>

        <div className="controls">
          <SearchBar 
            options={roots} 
            onSelect={handleSearchSelect} 
          />
          <button 
            className="btn btn-primary" 
            onClick={() => setShowCreateForm(true)}
          >
            + New Root Topic
          </button>
        </div>

        {showCreateForm && (
          <div className="modal-overlay">
            <div className="modal-content">
              <CreateTopic 
                onClose={handleNewRootCreated} 
                onCancel={() => setShowCreateForm(false)} 
              />
            </div>
          </div>
        )}

        <section className="roots-grid">
          {roots.length > 0 ? (
            roots.map(root => (
              <div key={root.id} className="root-card">
                <Link to={`/node/${root.id}`} className="root-link">
                  <div className="root-icon">
                    <span>ðŸ“š</span>
                  </div>
                  <div className="root-info">
                    <h3 className="root-title">{root.title}</h3>
                    <div className="root-meta">
                      <span className="sub-topics">Leafs: {root.leaf_count_in_subtree || 0}</span>
                      <span className="popularity">Popularity: {root.popularity}</span>
                      <span className="sub-topics">Score: {root.score || "N/A"}</span>
                    </div>
                  </div>
                </Link>
              </div>
            ))
          ) : (
            <div className="empty-state">
              <div className="empty-icon">ðŸŒ¿</div>
              <h3>No root nodes found</h3>
              <p>Be the first to create a root topic</p>
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default Home;
