import React from 'react';
import CreateComment from './CreateComment';
import getTimeDisplay from '../../utils/helperFunctions/GetTimeSince';
import { 
  fetchComments, 
  endorseComment,
  reportComment,
  createComment,
  unendorseComment,
  unreportComment as apiCreateComment
} from '../../services/commentsAPI';
import { useAuth } from '../../context/AuthContext';

const CommentsSection = ({ nodeId }) => {
  const [comments, setComments] = React.useState([]);
  const [showCommentForm, setShowCommentForm] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(true);
  const [processing, setProcessing] = React.useState({});
  const {user} = useAuth();

  // Fixed useEffect to handle falsy nodeId properly
  React.useEffect(() => {
    let isMounted = true;

    const loadComments = async () => {
      try {
        setIsLoading(true);
        if (!nodeId) {
          setComments([]);
          return;
        }

        const commentsData = await fetchComments(nodeId);
        if (isMounted) setComments(commentsData);
      } catch (error) {
        console.error('Error fetching comments:', error);
      } finally {
        if (isMounted) setIsLoading(false);
      }
    };

    loadComments();

    return () => {
      isMounted = false; // Flag to prevent state updates after unmount
    };
  }, [nodeId]);

  const handleNewComment = async (content) => {
    if (!content.trim()) return;

    try {
      // Create comment via API
      const newComment = await apiCreateComment(nodeId, content);
      // Optimistic update
      setComments(prev => [newComment, ...prev]);
      setShowCommentForm(false);
    } catch (error) {
      console.error('Error creating comment:', error);
    }
  };

  const handleEndorse = async (commentId) => {
  if (processing[commentId]) return;

  setProcessing(prev => ({...prev, [commentId]: 'endorsing'}));
  try {
    if (comments.find(c => c.id === commentId).is_endorsed) {
      await unendorseComment(nodeId, commentId, user.UUID);
      setComments(prev => prev.map(c => 
        c.id === commentId 
          ? {...c, is_endorsed: false, endorsement_count: Math.max(0, c.endorsement_count - 1)} 
          : c
      ));
    } else {
      await endorseComment(nodeId, commentId, user.UUID);
      setComments(prev => prev.map(c => 
        c.id === commentId 
          ? {...c, is_endorsed: true, endorsement_count: c.endorsement_count + 1} 
          : c
      ));
    }
  } finally {
    setProcessing(prev => ({...prev, [commentId]: undefined}));
  }
};

  // NEW: Handle reporting a comment
  const handleReport = async (commentId) => {
    if (processing[commentId]) return;

    setProcessing(prev => ({...prev, [commentId]: 'reporting'}));
    try {
      await reportComment(nodeId, commentId, user.UUID);
      // Update local state
      setComments(prev => prev.map(comment => 
        comment.id === commentId 
          ? {...comment, is_reported: true} 
          : comment
      ));
    } catch (error) {
      console.error('Error reporting comment:', error);
    } finally {
      setProcessing(prev => {
        const newState = {...prev};
        delete newState[commentId];
        return newState;
      });
    }
  };

  if (isLoading) return (
    <div className="d-flex justify-content-center my-5">
      <div className="spinner-border" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  );

  return (
    <div className="card mb-4">
      <div className="card-header bg-light d-flex justify-content-between align-items-center py-3">
        <h2 className="h5 mb-0">Attempts to solve</h2>
        <button 
          className={`btn btn-sm ${showCommentForm ? 'btn-outline-secondary' : 'btn-primary'}`}
          onClick={() => setShowCommentForm(!showCommentForm)}
        >
          {showCommentForm ? 'Cancel' : 'Add New'}
        </button>
      </div>

      {showCommentForm && (
        <div className="card-body border-bottom">
          <CreateComment 
            onCommentCreated={handleNewComment}
            nodeId={nodeId}
          />
        </div>
      )}

      <div className="list-group list-group-flush">
        {comments.length === 0 ? (
          <div className="text-center py-4">
            <p className="text-muted">No comments yet</p>
            <button 
              className="btn btn-link"
              onClick={() => setShowCommentForm(true)}
            >
              Be the first to add a comment
            </button>
          </div>
        ) : (
          comments.map(comment => (
            comment && (
              <div key={comment.id} className="list-group-item border-0">
                <div className="d-flex">
                  <div className="flex-shrink-0 me-3">
                    <div 
                      className="bg-light rounded-circle d-flex align-items-center justify-content-center" 
                      style={{width: '40px', height: '40px'}}
                    >
                      <span className="text-muted">U</span>
                    </div>
                  </div>
                  <div className="flex-grow-1">
                    <p className="mb-1">{comment.comment_text}</p>
                    <div className="d-flex justify-content-between align-items-center">
                      <small className="text-muted">
                        Posted {getTimeDisplay(comment.created_at)}
                      </small>
                      {/* NEW: Action buttons */}
                      <div className="d-flex gap-1">
                        <button
                          className={`btn btn-sm ${
                            comment.is_endorsed ? 'btn-success' : 'btn-outline-success'
                          }`}
                          onClick={() => handleEndorse(comment.id)}
                          disabled={processing[comment.id]}
                        >
                          {processing[comment.id] === 'endorsing' ? (
                            <span className="spinner-border spinner-border-sm" />
                          ) : (
                            `Endorse${comment.endorsement_count ? ` (${comment.endorsement_count})` : ''}`
                          )}
                        </button>
                        <button 
                          className={`btn btn-sm ${
                            comment.is_reported 
                              ? 'btn-danger disabled' 
                              : 'btn-outline-danger'
                          }`}
                          onClick={() => handleReport(comment.id)}
                          disabled={comment.is_reported || processing[comment.id]}
                        >
                          {processing[comment.id] === 'reporting' ? (
                            <span className="spinner-border spinner-border-sm" role="status" />
                          ) : (
                            `Report${comment.report_count > 0 ? ` (${comment.report_count})` : ''}`
                          )}
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )
          ))
        )}
      </div>
    </div>
  );
};

export default CommentsSection;
