import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const TreeNode = ({ node, depth = 0, maxDepth = 4 }) => {
  const [expanded, setExpanded] = useState(true);
  const navigate = useNavigate();

  if (!node) return null;
  if (depth >= maxDepth) return null;

  const hasChildren = node.children && node.children.length > 0;

  return (
    <div className="tree-node" style={{ marginLeft: `${depth * 16}px` }}>
      <div className="node-content d-flex align-items-center">
        {hasChildren && (
          <button 
            className="btn btn-sm btn-link p-0 me-1"
            onClick={() => setExpanded(!expanded)}
            aria-label={expanded ? "Collapse" : "Expand"}
          >
            {expanded ? '▼' : '►'}
          </button>
        )}
        <div 
          className="flex-grow-1 p-2 mb-2 border rounded bg-light cursor-pointer"
          onClick={() => navigate(`/nodes/${node.id}`)}
        >
          <div className="d-flex justify-content-between align-items-center">
            <span className="fw-bold">{node.name}</span>
            {hasChildren && (
              <span className="badge bg-secondary">
                {node.children.length}
              </span>
            )}
          </div>
        </div>
      </div>

      {hasChildren && expanded && (
        <div className="node-children">
          {node.children.map(child => (
            <TreeNode 
              key={child.id} 
              node={child} 
              depth={depth + 1} 
              maxDepth={maxDepth} 
            />
          ))}
        </div>
      )}
    </div>
  );
};

const TreeVisualization = ({ treeData, maxDepth = 4 }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  if (!treeData) return (
    <div className="tree-visualization my-4">
      <h4 className="d-flex justify-content-between align-items-center mb-3">
        Hierarchy Tree
      </h4>
      <div className="text-center p-4 border rounded bg-light">
        Loading tree data...
      </div>
    </div>
  );

  return (
    <div className="tree-visualization my-4">
      <h4 className="d-flex justify-content-between align-items-center mb-3">
        Hierarchy Tree
        <button 
          className="btn btn-sm btn-outline-secondary"
          onClick={() => setIsExpanded(!isExpanded)}
        >
          {isExpanded ? 'Collapse All' : 'Expand All'}
        </button>
      </h4>
      <div className="tree-container bg-white p-3 rounded border">
        <TreeNode 
          node={treeData} 
          depth={0} 
          maxDepth={maxDepth} 
          expanded={isExpanded}
        />
      </div>
    </div>
  );
};

export default TreeVisualization;
