import React from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import CreateComment from '../components/CreateComment';

const NodePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [node, setNode] = React.useState(null);
  const [children, setChildren] = React.useState([]);
  const [path, setPath] = React.useState([]);
  const [comments, setComment] = React.useState([]);

  // Fetch node data
  React.useEffect(() => {
    const fetchData = async () => {
      try {
        const [nodeRes, childrenRes, pathRes, commentRes, createCommentRes] = await Promise.all([
          fetch(`http://localhost:5000/api/nodes/${id}`),
          fetch(`http://localhost:5000/api/nodes/${id}/children`),
          fetch(`http://localhost:5000/api/nodes/${id}/path`),
          fetch(`http://localhost:5000/api/nodes/${id}/comments`),
        ]);

        setNode(await nodeRes.json());
        setChildren(await childrenRes.json());
        setPath((await pathRes.json()).reverse());
        setComment((await commentRes.json()));
        setCreateComment((await createCommentRes.json()));

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [id]);
  if (!node) return <div>Loading...</div>;
  const handleNewComment = (comment) => {
    setComments([...comments, comment]);
  };

  return (
    <div>
      {/* Breadcrumb Navigation */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          {path.map(n => (
            <li key={n.id} className="breadcrumb-item">
              <Link to={`/node/${n.id}`}>Node {n.id}</Link>
            </li>
          ))}
          <li className="breadcrumb-item active" aria-current="page">
            Node {node.id}
          </li>
        </ol>
      </nav>
      {/* Node Details */}
      <h1>Node {node.id}</h1>
      <p className="lead">Popularity: {node.popularity}</p>

      {/* Comment List */}
      <div className="card mt-4">
        <div className="card-header">
          <h2>Attempts to solve</h2>
        </div>
        <ul className="list-group list-group-flush">
          {comments.map(comment => (
              comment.content
          ))}
        </ul>
        <CreateComment topicId={id} onNewComment={handleNewComment} />
      </div>

      {/* Children List */}
      <div className="card mt-4">
        <div className="card-header">
          <h2>Children Nodes</h2>
        </div>
        <ul className="list-group list-group-flush">
          {children.map(child => (
            <li key={child.id} className="list-group-item">
              <Link to={`/node/${child.id}`} className="btn btn-link">
                Node {child.id} (Score: {child.popularity})
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Action Buttons */}
      <div className="mt-4">
        <button className="btn btn-secondary" onClick={() => navigate(`/node/${id}/newnode`)}>
            Create New True False Statement
        </button>
        {node.parent_id && (
          <button className="btn btn-secondary" onClick={() => navigate(`/node/${node.parent_id}`)}>
            Back to Parent
          </button>
        )}
        <button className="btn btn-info" onClick={() => navigate('/')}>
          Back to Roots
        </button>
      </div>

    </div>
  );
};

export default NodePage;
