// topicApi.js
import axios from 'axios';

const BASE_URL = 'http://localhost:5000/api';

export const createTopic = async (title, content) => {
  try {
    const response = await axios.post(
      `${BASE_URL}/roots`,
      { title, content },
      {
        headers: { 'Content-Type': 'application/json' }
      }
    );
    return response.data;
  } catch (error) {
    throw error;
  }
};
