// src/services/commentsAPI.js
const BASE_URL = 'http://localhost:5000/api/nodes';

export const fetchComments = async (nodeId) => {
  const response = await fetch(`${BASE_URL}/${nodeId}/comments`);
  if (!response.ok) throw new Error('Network response was not ok');
  return response.json();
};

export const refreshComments = async (nodeId) => {
  const response = await fetch(`${BASE_URL}/${nodeId}/comments`);
  if (!response.ok) throw new Error('Failed to refresh comments');
  return response.json();
};

export const createComment = async (nodeId, content) => {
  try {
    const response = await fetch(`${BASE_URL}/${nodeId}/comments`, {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({ content })
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'API request failed');
    }

    return await response.json();
  } catch (error) {
    console.error('API call error:', error);
    throw error; // Rethrow to let component handle it
  }
};

export const endorseComment = async (nodeId, commentId, userId) => {
  try {
    const response = await fetch(`${BASE_URL}/${nodeId}/comments/${commentId}/endorse`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({userId})
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Failed to endorse comment');
    }

    return await response.json();
  } catch (error) {
    console.error('Endorse error:', error);
    throw error;
  }
};

// NEW: Report comment endpoint
export const reportComment = async (nodeId, commentId, userId) => {
  try {
    const response = await fetch(`${BASE_URL}/${nodeId}/comments/${commentId}/report`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({userId})
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Failed to report comment');
    }

    return await response.json();
  } catch (error) {
    console.error('Report error:', error);
    throw error;
  }
};
