import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import NodeDetails from '../NodePageComponents/components/NodePageComponents/NodeDetails';
import CommentsSection from '../components/NodePageComponents/CommentsSection';
import ChildrenList from '../components/NodePageComponents/ChildrenList';
import ActionButtons from '../components/NodePageComponents/ActionButtons';
import Breadcrumb from '../components/NodePageComponents/Breadcrumb';
import '../css/NodePage.css';

const NodePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [node, setNode] = React.useState(null);
  const [children, setChildren] = React.useState([]);
  const [path, setPath] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);

  // Fetch node data
  React.useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const [nodeRes, childrenRes, pathRes] = await Promise.all([
          fetch(`http://localhost:5000/api/nodes/${id}`),
          fetch(`http://localhost:5000/api/nodes/${id}/children`),
          fetch(`http://localhost:5000/api/nodes/${id}/path`)
        ]);

        setNode(await nodeRes.json());
        setChildren(await childrenRes.json());
        setPath((await pathRes.json()).reverse());
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (isLoading) return (
    <div className="d-flex justify-content-center my-5">
      <div className="spinner-border" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  );

  if (!node) return <div className="alert alert-danger">Node not found</div>;

  return (
    <div className="container py-4">
      <Breadcrumb path={path} />
      <NodeDetails node={node} />
      <CommentsSection nodeId={id} />
      <ChildrenList children={children} />
      <ActionButtons node={node} navigate={navigate} nodeId={id} />
    </div>
  );
};

export default NodePage;
