import React, { useEffect, useRef, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';
import Dashboard from '../components/Dashboard';
import '../css/App.css';
import '../css/SearchBar.css'

// New SearchBar Component
const SearchBar = ({ options, onSelect }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const searchBarRef = useRef(null);

  const filteredOptions = options.filter(option =>
    option.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleClickOutside = (event) => {
    if (searchBarRef.current && !searchBarRef.current.contains(event.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="search-container" ref={searchBarRef}>
      <div 
        className="search-bar" 
        onClick={() => setIsOpen(true)}
      >
        üîç Search topics...
      </div>

      {isOpen && (
        <div className="search-popup">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            autoFocus
            className="search-input"
          />
          <div className="search-results">
            {filteredOptions.length > 0 ? (
              filteredOptions.map(option => (
                <div 
                  key={option.id} 
                  className="search-result-item"
                  onClick={() => {
                    onSelect(option);
                    setIsOpen(false);
                  }}
                >
                  <span className="search-result-icon">üìö</span>
                  <span className="search-result-text">{option.title}</span>
                </div>
              ))
            ) : (
              <div className="no-results">No topics found</div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};
export default SearchBar;
