import React from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import CreateComment from '../components/CreateComment';

const NodePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [node, setNode] = React.useState(null);
  const [children, setChildren] = React.useState([]);
  const [path, setPath] = React.useState([]);
  const [comments, setComments] = React.useState([]);
  const [showCommentForm, setShowCommentForm] = React.useState(false); // New state for form visibility

  // Fetch node data
  React.useEffect(() => {
    const fetchData = async () => {
      try {
        const [nodeRes, childrenRes, pathRes, commentRes] = await Promise.all([
          fetch(`http://localhost:5000/api/nodes/${id}`),
          fetch(`http://localhost:5000/api/nodes/${id}/children`),
          fetch(`http://localhost:5000/api/nodes/${id}/path`),
          fetch(`http://localhost:5000/api/nodes/${id}/comments`),
        ]);

        setNode(await nodeRes.json());
        setChildren(await childrenRes.json());
        setPath((await pathRes.json()).reverse());
        setComments(await commentRes.json()); // Fixed variable name
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [id]);

  // Function to refresh comments after submission
  const refreshComments = async () => {
    try {
      const commentRes = await fetch(`http://localhost:5000/api/nodes/${id}/comments`);
      setComments(await commentRes.json());
      setShowCommentForm(false); // Hide form after submission
    } catch (error) {
      console.error('Error refreshing comments:', error);
    }
  };

  if (!node) return <div>Loading...</div>;

  return (
    <div>
      {/* Breadcrumb Navigation */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          {path.map(n => (
            <li key={n.id} className="breadcrumb-item">
              <Link to={`/node/${n.id}`}>Node {n.id}</Link>
            </li>
          ))}
          <li className="breadcrumb-item active" aria-current="page">
            Node {node.id}
          </li>
        </ol>
      </nav>

      {/* Node Details */}
      <h1>Node {node.id}</h1>
      <p className="lead">Popularity: {node.popularity}</p>

      {/* Comment Section */}
      <div className="card mt-4">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h2>Attempts to solve</h2>
          {/* Add Comment Button */}
          <button 
            className="btn btn-primary"
            onClick={() => setShowCommentForm(!showCommentForm)}
          >
            {showCommentForm ? 'Cancel' : 'Add Comment'}
          </button>
        </div>

        {/* Comment Form */}
        {showCommentForm && (
          <div className="card-body">
            <CreateComment 
              nodeId={id} 
              onCommentCreated={refreshComments} 
            />
          </div>
        )}

        {/* Comment List */}
        <ul className="list-group list-group-flush">
          {comments.map(comment => (
            <li key={comment.id} className="list-group-item">
              {comment.content}
            </li>
          ))}
        </ul>
      </div>

      {/* ... (rest of your existing code) ... */}
    </div>
  );
};

export default NodePage;
