// ... existing imports ...
import TreeVisualization from '../components/NodePageComponents/TreeVisualization';
import ErrorMessage from '../components/Common/ErrorMessage';
import '../css/NodePage.css';

const NodePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [node, setNode] = React.useState(null);
  const [children, setChildren] = React.useState([]);
  const [path, setPath] = React.useState([]);
  const [treeData, setTreeData] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);

  // Fetch all node data
  React.useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        const urls = [
          `http://localhost:5000/api/nodes/${id}`,
          `http://localhost:5000/api/nodes/${id}/children`,
          `http://localhost:5000/api/nodes/${id}/path`,
          `http://localhost:5000/api/nodes/${id}/tree?depth=4`
        ];

        const responses = await Promise.all(urls.map(url => fetch(url)));
        const results = await Promise.all(responses.map(res => {
          if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
          return res.json();
        }));

        setNode(results[0]);
        setChildren(results[1]);
        setPath(results[2].reverse());
        setTreeData(results[3]);

      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Failed to load node data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) return (
    <div className="d-flex justify-content-center my-5">
      <div className="spinner-border" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  );

  if (error) return <ErrorMessage message={error} onRetry={() => window.location.reload()} />;
  if (!node) return <div className="alert alert-danger">Node not found</div>;

  return (
    <div className="container py-4">
      <Breadcrumb path={path} />
      <NodeDetails node={node} />
      <TreeVisualization 
        treeData={treeData} 
        maxDepth={4}
      />
      <CommentsSection nodeId={id} />
      <ChildrenList children={children} />
      <ActionButtons node={node} navigate={navigate} nodeId={id} />
    </div>
  );
};

export default NodePage;
