import React from 'react';
import { 
  fetchComments,
  endorseComment,
  unendorseComment,
  reportComment,
  unreportComment,
  createComment
} from '../../services/commentsAPI';
import { useAuth } from '../../context/AuthContext';

const useCommentsLogic = (nodeId) => {
  const [comments, setComments] = React.useState([]);
  const [showCommentForm, setShowCommentForm] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(true);
  const [processing, setProcessing] = React.useState({});
  const { user } = useAuth();

  React.useEffect(() => {
    let isMounted = true;

    const loadComments = async () => {
      try {
        setIsLoading(true);
        if (!nodeId) {
          setComments([]);
          return;
        }

        const userId = user?.UUID || null;
        const commentsData = await fetchComments(nodeId, userId);
        if (isMounted) setComments(commentsData);
      } catch (error) {
        console.error('Error fetching comments:', error);
      } finally {
        if (isMounted) setIsLoading(false);
      }
    };

    loadComments();

    return () => { isMounted = false; };
  }, [nodeId, user]);

  const handleNewComment = async (content) => {
    try {
      const newComment = await createComment(nodeId, content);
      setComments(prev => [newComment, ...prev]);
      setShowCommentForm(false);
    } catch (error) {
      console.error('Error creating comment:', error);
    }
  };

  const handleEndorse = async (commentId) => {
    if (processing[commentId] || !user) return;

    setProcessing(prev => ({...prev, [commentId]: 'endorsing'}));
    try {
      const isEndorsed = comments.find(c => c.id === commentId)?.is_endorsed;

      if (isEndorsed) {
        await unendorseComment(nodeId, commentId, user.UUID);
      } else {
        await endorseComment(nodeId, commentId, user.UUID);
      }

      setComments(prev => prev.map(comment => 
        comment.id === commentId 
          ? { 
              ...comment, 
              is_endorsed: !isEndorsed,
              endorsement_count: isEndorsed 
                ? comment.endorsement_count - 1
                : comment.endorsement_count + 1
            } 
          : comment
      ));
    } catch (error) {
      console.error('Error toggling endorsement:', error);
    } finally {
      setProcessing(prev => {
        const newState = {...prev};
        delete newState[commentId];
        return newState;
      });
    }
  };

  const handleReport = async (commentId) => {
    if (processing[commentId] || !user) return;

    setProcessing(prev => ({...prev, [commentId]: 'reporting'}));
    try {
      const isReported = comments.find(c => c.id === commentId)?.is_reported;

      if (isReported) {
        await unreportComment(nodeId, commentId, user.UUID);
      } else {
        await reportComment(nodeId, commentId, user.UUID);
      }

      setComments(prev => prev.map(comment => 
        comment.id === commentId 
          ? { 
              ...comment, 
              is_reported: !isReported,
              report_count: isReported 
                ? Math.max(0, comment.report_count - 1)
                : comment.report_count + 1
            } 
          : comment
      ));
    } catch (error) {
      console.error('Error toggling report:', error);
    } finally {
      setProcessing(prev => {
        const newState = {...prev};
        delete newState[commentId];
        return newState;
      });
    }
  };

  return {
    comments,
    showCommentForm,
    isLoading,
    processing,
    user,
    setShowCommentForm,
    handleNewComment,
    handleEndorse,
    handleReport
  };
};

export default useCommentsLogic;
