import { useEffect, useState, createContext, useContext, useRef } from 'react';
import { fetchUserTheme, updateThemePreference } from '../services/themeAPI';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [currentTheme, setCurrentTheme] = useState('light');
  const [isLoading, setIsLoading] = useState(true);

  // Apply theme to DOM
  const applyTheme = (theme) => {
    document.documentElement.setAttribute('data-theme', theme);
  };


  useEffect(() => {
  const initializeTheme = async () => {
    setIsLoading(true);
    try {
      const savedTheme = await fetchUserTheme();
      setCurrentTheme(savedTheme);
      applyTheme(savedTheme);
    } catch (err) {
      console.error('Theme loading error:', err);
      // Use media query with fallback
      const systemPrefersDark = window.matchMedia?.('(prefers-color-scheme: dark)').matches;
      setCurrentTheme(systemPrefersDark ? 'dark' : 'light');
    }
    setIsLoading(false);
  };

  initializeTheme();
}, []);

  // Set new theme
  const setTheme = async (themeName) => {
  const previousTheme = currentTheme;
  try {
    setCurrentTheme(themeName);
    applyTheme(themeName);

    await updateThemePreference(themeName);
  } catch (error) {
    console.error("Failed to save theme:", error);
    // Revert UI immediately on error
    setCurrentTheme(previousTheme);
    applyTheme(previousTheme);

    // Add user feedback
    alert("Failed to save theme preference. Please try again.");
  }
};

  return (
    <ThemeContext.Provider value={{ 
      currentTheme, 
      setTheme,
      isLoading
    }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
