import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";
import PrivateRoute from '../../CodeLoginAuth/components/PrivateRoute';
import { createComment } from "../services/commentsAPI";

const CreateComment = ({ onCommentCreated }) => {
  const { id } = useParams();
  const [content, setContent] = useState('');
  const [proStatus, setProStatus] = useState(null); // 'true' for proves, 'false' for disproves
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
  e.preventDefault();
  if (proStatus === null) {
    setError('Please specify if this comment proves or disproves the root node');
    return;
  }

  setIsSubmitting(true);
  setError('');

  try {
    const createdComment = await createComment(id, content, proStatus);

    if (onCommentCreated) {
      onCommentCreated(createdComment); // Parent handles UI update
    } else {
      navigate(`/node/${id}`); // Navigate without reloading
    }

    // Reset form state instead of reloading
    setContent('');
    setProStatus(null);
  } catch (error) {
    setError(error.message || 'Failed to create comment');
  } finally { // Guaranteed cleanup
    setIsSubmitting(false);
  }
};

  return (
    <PrivateRoute>
      <div className="topic-form p-4 border rounded shadow-sm bg-white">
        <h2 className="mb-3">Create New Comment</h2>

        {error && (
          <div className="alert alert-danger mb-3">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Comment</label>
            <textarea
              className="form-control"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="Enter your comment"
              required
              disabled={isSubmitting}
              rows={4}
            />
          </div>

          {/* NEW: Radio buttons for proStatus */}
          <div className="mb-3">
            <label className="form-label">
              Does this comment prove or disprove the root node?
            </label>
            <div className="mt-2">
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="proStatus"
                  id="proves"
                  value="true"
                  checked={proStatus === true}
                  onChange={() => setProStatus(true)}
                  required
                />
                <label className="form-check-label" htmlFor="proves">
                  Proves
                </label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="proStatus"
                  id="disproves"
                  value="false"
                  checked={proStatus === false}
                  onChange={() => setProStatus(false)}
                />
                <label className="form-check-label" htmlFor="disproves">
                  Disproves
                </label>
              </div>
            </div>
          </div>

          <div className="d-flex justify-content-end gap-2">
            <button
              type="submit"
              className="btn btn-primary"
              disabled={isSubmitting}
            >
              {isSubmitting ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" aria-hidden="true"></span>
                  Submitting...
                </>
              ) : 'Submit Comment'}
            </button>
          </div>
        </form>
      </div>
    </PrivateRoute>
  );
};

export default CreateComment;
