import { useEffect, useState, createContext, useContext } from 'react';
import { fetchUserTheme, updateThemePreference } from '../services/themeAPI';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const loadTheme = async () => {
      try {
        const savedTheme = await fetchUserTheme();
        setDarkMode(savedTheme);
        applyTheme(savedTheme);
      } catch (err) {
        const systemPrefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
        setDarkMode(systemPrefersDark);
        applyTheme(systemPrefersDark);
      }
    };
    loadTheme();
  }, []);

  const setTheme = async (isDark) => {
    setDarkMode(isDark);
    applyTheme(isDark);
    await updateThemePreference(isDark); // API call to save preference
  };

  const applyTheme = (isDark) => {
    document.body.classList.toggle('dark-mode', isDark);
    document.body.classList.toggle('light-mode', !isDark);
  };

  const toggleTheme = () => setTheme(!darkMode);

  return (
    <ThemeContext.Provider value={{ 
      darkMode, 
      toggleTheme,
      setTheme // Expose new setter function
    }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
