import React from 'react';
import PropTypes from 'prop-types';

// Helper function to darken colors
function getDarkenedColor(hex) {
    hex = hex.replace(/^#/, '');
    if (hex.length === 3) {
        hex = hex[0] + hex[0] + hex[1] + hex[1] + hex[2] + hex[2];
    }
    const r = parseInt(hex.substring(0, 2), 16);
    const g = parseInt(hex.substring(2, 4), 16);
    const b = parseInt(hex.substring(4, 6), 16);

    // Darken by 20%
    const darken = (c) => Math.max(0, Math.floor(c * 0.8));
    return `#${darken(r).toString(16).padStart(2, '0')}${darken(g).toString(16).padStart(2, '0')}${darken(b).toString(16).padStart(2, '0')}`;
}

const ScoreBar = ({ 
  score, 
  max = 100, 
  height = 12,
  color = "var(--accent-primary)",
  label,
  showValueInside = false,
  isCompleted = false
}) => {
  if (score === null || score === -1) {
    return (
      <div className="score-na-message">
        Score: N/A (No completed Leafs yet)
      </div>
    );
  }

  const percentage = Math.max(0, Math.min(max, score * 100 || 0)) / max;
  const barHeight = height;

  // Generate gradient colors for completed state
  let background;
  if (isCompleted) {
    const darkened = getDarkenedColor(color);
    background = `linear-gradient(to right, ${darkened}99, ${darkened})`;
  } else {
    background = `linear-gradient(to right, ${color}99, ${color})`;
  }

  // Bar fill styles
  const fillStyles = {
    width: `${percentage * 100}%`,
    height: '100%',
    background: background,
    borderRadius: 'inherit',
    transition: 'width 0.3s ease',
  };

  return (
    <div className="score-bar-container">
      {label && <div className="score-bar-label">{label}</div>}

      <div 
        className="score-bar-background"
        style={{ height: barHeight }}
      >
        <div className="score-bar-fill" style={fillStyles}>
          {showValueInside && percentage > 0.3 && (
            <span className="score-bar-inside-value">
              {Math.round(score * 1000) / 10}%
            </span>
          )}
        </div>
        {isCompleted && (
          <div className="score-bar-completed-badge">
            (completed)
          </div>
        )}
      </div>

      {!showValueInside && (
        <div className="score-bar-value">
          {Math.round(score * 1000) / 10}%
        </div>
      )}
    </div>
  );
};

ScoreBar.propTypes = {
  score: PropTypes.number,
  max: PropTypes.number,
  height: PropTypes.number,
  color: PropTypes.string,
  label: PropTypes.string,
  showValueInside: PropTypes.bool,
  isCompleted: PropTypes.bool
};

export default ScoreBar;
