import React, { useState, useEffect } from 'react';
import ScoreBar from '../../utils/visuals/ScoreBar';
import '../nonGlobalStyle/NodeDetails.css';
import axios from 'axios';

const NodeDetails = ({ node, user }) => {
  const [currentNode, setCurrentNode] = useState(node);
  const [userReaction, setUserReaction] = useState(null);
  const [loading, setLoading] = useState(false);

  // Initialize reaction state when component mounts or node changes
  useEffect(() => {
    if (node.userReaction !== undefined) {
      setUserReaction(node.userReaction);
    }
  }, [node]);

  const handleReaction = async (reactionType) => {
    if (!user || !user.UUID) {
      alert('Please login to react');
      return;
    }

    setLoading(true);
    try {
      let endpoint = '';
      let newReaction = null;

      if (reactionType === 1) { // Like
        if (userReaction === 1) {
          endpoint = `unlike/${user.UUID}`;
          newReaction = null;
        } else {
          endpoint = `like/${user.UUID}`;
          newReaction = 1;
        }
      } else { // Dislike
        if (userReaction === 2) {
          endpoint = `undislike/${user.UUID}`;
          newReaction = null;
        } else {
          endpoint = `dislike/${user.UUID}`;
          newReaction = 2;
        }
      }

      await axios.post(`/api/nodes/${node.id}/${endpoint}`);

      // Optimistic UI update
      setUserReaction(newReaction);
      setCurrentNode(prev => ({
        ...prev,
        likes: reactionType === 1 
          ? (userReaction === 1 ? prev.likes - 1 : (userReaction === 2 ? prev.likes + 1 : prev.likes + 1))
          : prev.likes,
        dislikes: reactionType === 2 
          ? (userReaction === 2 ? prev.dislikes - 1 : (userReaction === 1 ? prev.dislikes + 1 : prev.dislikes + 1))
          : prev.dislikes
      }));

    } catch (error) {
      console.error('Error updating reaction:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="node-detail mb-4">
      <div className="card-body">
        <h1 className="card-title text-accent">
          {currentNode.title || `Node ${currentNode.id}`}: 
          {currentNode.statementTrueFalseFlag === null
            ? " Current statement to Prove" 
            : currentNode.statementTrueFalseFlag 
              ? " True" 
              : " False"
          }
        </h1>
        <p className="lead text-secondary">Explanation: {currentNode.content}</p>
        <p className="lead text-secondary">Popularity: {currentNode.popularity}</p>
        <p className="lead text-secondary">You are: {user?.username || 'Guest'}</p>

        {/* Reaction Buttons */}
        <div className="reaction-buttons mt-3 mb-3">
          <button 
            className={`btn ${userReaction === 1 ? 'btn-success' : 'btn-outline-success'} me-2`}
            onClick={() => handleReaction(1)}
            disabled={loading}
          >
            üëç Like ({currentNode.likes || 0})
          </button>
          <button 
            className={`btn ${userReaction === 2 ? 'btn-danger' : 'btn-outline-danger'}`}
            onClick={() => handleReaction(2)}
            disabled={loading}
          >
            üëé Dislike ({currentNode.dislikes || 0})
          </button>
        </div>

        <ScoreBar 
          score={currentNode.popularity} 
          height={10}
          color="#f0ac29"
          label="Popularity"
        />

        <ScoreBar 
          score={currentNode.score} 
          height={10}
          color="#4285F4"
          label="Weighted Average Score"
        />
      </div>
    </div>
  );
};

export default NodeDetails;
