import React from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import CreateComment from '../components/CreateComment';
import  '../css/NodePage.css'

const NodePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [node, setNode] = React.useState(null);
  const [children, setChildren] = React.useState([]);
  const [path, setPath] = React.useState([]);
  const [comments, setComments] = React.useState([]);
  const [showCommentForm, setShowCommentForm] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(true);

  // Fetch node data
  React.useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const [nodeRes, childrenRes, pathRes, commentRes] = await Promise.all([
          fetch(`http://localhost:5000/api/nodes/${id}`),
          fetch(`http://localhost:5000/api/nodes/${id}/children`),
          fetch(`http://localhost:5000/api/nodes/${id}/path`),
          fetch(`http://localhost:5000/api/nodes/${id}/comments`),
        ]);

        setNode(await nodeRes.json());
        setChildren(await childrenRes.json());
        setPath((await pathRes.json()).reverse());

        const commentsData = await commentRes.json();
        setComments(commentsData);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [id]);

  // Function to refresh comments after submission
  const refreshComments = async (newComment) => {
    try {
      // Optimistically update the UI
      setComments(prevComments => [newComment, ...prevComments]);
      setShowCommentForm(false);

      // Then refresh the data from the server
      const commentRes = await fetch(`http://localhost:5000/api/nodes/${id}/comments`);
      setComments(await commentRes.json());
    } catch (error) {
      console.error('Error refreshing comments:', error);

      // If server refresh fails, revert to previous state
      setComments(prevComments => prevComments.filter(c => c.id !== newComment.id));
    }
  };

  if (isLoading) return <div className="d-flex justify-content-center my-5">
    <div className="spinner-border" role="status">
      <span className="visually-hidden">Loading...</span>
    </div>
  </div>;

  if (!node) return <div className="alert alert-danger">Node not found</div>;

  return (
    <div className="container py-4">
      {/* Breadcrumb Navigation */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          {path.map(n => (
            <li key={n.id} className="breadcrumb-item">
              <Link 
                to={`/node/${n.id}`} 
                className="text-decoration-none"
                style={{ color: n.flag ? 'green' : 'red' }} // Dynamic color
              >
                {n.title || `Node ${n.id}`}
              </Link>
            </li>
          ))}
          <li className="breadcrumb-item active" aria-current="page">
            {node.title || `Node ${node.id}`}
          </li>
        </ol>
      </nav>

      {/* Node Details */}
      <div className="card mb-4">
        <div className="card-body">
          <h1 className="card-title">{node.title || `Node ${node.id}`}</h1>
          <p className="lead text-muted">Popularity: {node.popularity}</p>
        </div>
      </div>

      {/* Comment Section */}
      <div className="card mb-4">
        <div className="card-header bg-light d-flex justify-content-between align-items-center py-3">
          <h2 className="h5 mb-0">Attempts to solve</h2>
          <button 
            className={`btn btn-sm ${showCommentForm ? 'btn-outline-secondary' : 'btn-primary'}`}
            onClick={() => setShowCommentForm(!showCommentForm)}
          >
            {showCommentForm ? 'Cancel' : 'Add New'}
          </button>
        </div>

        {/* Comment Form */}
        {showCommentForm && (
          <div className="card-body border-bottom">
            <CreateComment 
              onCommentCreated={refreshComments} 
            />
          </div>
        )}

        {/* Comment List */}
        <div className="list-group list-group-flush">
          {comments.length === 0 ? (
            <div className="text-center py-4">
              <p className="text-muted">No comments yet</p>
              <button 
                className="btn btn-link"
                onClick={() => setShowCommentForm(true)}
              >
                Be the first to add a comment
              </button>
            </div>
          ) : (
            comments.map(comment => (
              <div key={comment.id} className="list-group-item border-0">
                <div className="d-flex">
                  <div className="flex-shrink-0 me-3">
                    <div className="bg-light rounded-circle d-flex align-items-center justify-content-center" 
                         style={{width: '40px', height: '40px'}}>
                      <span className="text-muted">U</span>
                    </div>
                  </div>
                  <div className="flex-grow-1">
                    <p className="mb-1">{comment.comment_text}</p>
                    <small className="text-muted">Posted just now</small>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Children List */}
      <div className="card mb-4">
        <div className="card-header bg-light py-3">
          <h2 className="h5 mb-0">Children Nodes</h2>
        </div>
        <div className="list-group list-group-flush">
          {children.length === 0 ? (
            <div className="list-group-item text-center py-3 text-muted">
              No child nodes
            </div>
          ) : (
            children.map(child => (
              <Link 
                key={child.id} 
                to={`/node/${child.id}`}
                className="list-group-item list-group-item-action d-flex justify-content-between align-items-center"
              >
                <span className={child.statementTrueFalseFlag ? 'true-color' : 'false-color'} >
                  {child.title || `Node ${child.id}`} {child.statementTrueFalseFlag ? "True" : "False"} 
                </span>
                <span className="badge bg-primary rounded-pill">
                  Popularity: {child.popularity}
                </span>
              </Link>
            ))
          )}
        </div>
      </div>

      {/* Action Buttons */}
      <div className="d-grid gap-2 d-md-flex justify-content-md-end mt-4">
        <button 
          className="btn btn-secondary me-md-2" 
          onClick={() => navigate(`/node/${id}/newnode`)}
        >
          Create New True/False Statement
        </button>

        {node.parent_id && (
          <button 
            className="btn btn-outline-secondary me-md-2" 
            onClick={() => navigate(`/node/${node.parent_id}`)}
          >
            Back to Parent
          </button>
        )}

        <button 
          className="btn btn-outline-info" 
          onClick={() => navigate('/')}
        >
          Back to Roots
        </button>
      </div>
    </div>
  );
};

export default NodePage;
