import React from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';

const RootsPage = () => {
  const { id } = useParams();
  const [roots, setRoots] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [showCreateForm, setShowCreateForm] = React.useState(false); // Added state for conditional rendering
  const navigate = useNavigate(); // Added for navigation

  React.useEffect(() => {
    const fetchRoots = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/roots/${id}`);
        const data = await response.json();

        if (Array.isArray(data)) {
          setRoots(data);
        } else {
          console.error('API response is not an array:', data);
          setRoots([]);
        }
      } catch (error) {
        console.error('Error fetching root nodes:', error);
        setRoots([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRoots();
  }, [id]);

  // Handle form closure
  const handleFormClose = () => {
    setShowCreateForm(false);
    navigate(`/roots/${id}`); // Refresh data
  };

  if (loading) return <div>Loading root nodes...</div>;

  return (
    <div>
      <h1>Root Nodes</h1>
      <ul className="list-group">
        {roots.length > 0 ? (
          roots.map(root => (
            <li key={root.id} className="list-group-item">
              <Link to={`/node/${root.id}`}>
                Node {root.id} (Popularity: {root.popularity})
              </Link>
            </li>
          ))
        ) : (
          <li className="list-group-item">No root nodes found</li>
        )}
      </ul>

      {/* Fixed button with proper event handling */}
      <button 
        className="btn btn-primary me-2" 
        onClick={() => setShowCreateForm(true)}
      >
        Create Root Node
      </button>

      {/* Conditionally render CreateTopic component */}
      {showCreateForm && (
        <CreateTopic 
          onClose={handleFormClose} 
          parentId={id} 
        />
      )}
    </div>
  );
};

export default RootsPage;
