// services/themeService.js
import axios from 'axios';

export const updateThemePreference = async (themeFlag) => {
  const token = localStorage.getItem('token');
  await axios.put('/api/theme', { themeFlag }, {
    headers: { Authorization: `Bearer ${token}` }
  });
};

export const fetchUserTheme = async () => {
  const token = localStorage.getItem('token');
  const response = await axios.get('/api/theme', {
    headers: { Authorization: `Bearer ${token}` }
  });
  return response.data.darkMode;
};
