import React from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';

const RootsPage = () => {
  const { id } = useParams();
  const [roots, setRoots] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [showCreateForm, setShowCreateForm] = React.useState(false);

  const fetchRoots = React.useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/roots/${id}`);
      const data = await response.json();
      setRoots(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching root nodes:', error);
      setRoots([]);
    } finally {
      setLoading(false);
    }
  }, [id]);

  React.useEffect(() => {
    fetchRoots();
  }, [fetchRoots]);

  const handleNewRootCreated = () => {
    setShowCreateForm(false);
    fetchRoots();  // Refresh root list
  };

  if (loading) return <div>Loading root nodes...</div>;

  return (
    <div>
      <h1>Root Nodes</h1>
      <button className="btn btn-secondary" onClick={() => navigate(`/topics/new`)}>
            Back to Parent
      </button>

      {showCreateForm ? (
        <CreateTopic 
          onClose={handleNewRootCreated} 
          onCancel={() => setShowCreateForm(false)} 
        />
      ) : (
        <ul className="list-group">
          {roots.length > 0 ? (
            roots.map(root => (
              <li key={root.id} className="list-group-item">
                <Link to={`/node/${root.id}`}>
                  {root.title} (Popularity: {root.popularity})
                </Link>
              </li>
            ))
          ) : (
            <li className="list-group-item">No root nodes found</li>
          )}
        </ul>
      )}
    </div>
  );
};

export default RootsPage;
