import React from 'react';
import CreateComment from './CreateComment';
import CommentItem from './CommentItem';
import useCommentsLogic from './useCommentsLogic';
import { useAuth } from '../../CodeLoginAuth/context/AuthContext';


const CommentsSection = ({ nodeId }) => {
  const {
    comments,
    showCommentForm,
    isLoading,
    processing,
    user,
    setShowCommentForm,
    handleNewComment,
    handleEndorse,
    handleReport
  } = useCommentsLogic(nodeId);

  if (isLoading) return (
    <div className="d-flex justify-content-center my-5">
      <div className="spinner-border" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  );

  return (
    <div className="card mb-4">
      <div className="card-header bg-light d-flex justify-content-between align-items-center py-3">
        <h2 className="h5 mb-0">Attempts to solve</h2>
        <button 
          className={`btn btn-sm ${showCommentForm ? 'btn-outline-secondary' : 'btn-primary'}`}
          onClick={() => setShowCommentForm(!showCommentForm)}
          disabled={!nodeId}
        >
          {showCommentForm ? 'Cancel' : 'Add New'}
        </button>
      </div>

      {showCommentForm && (
        <div className="card-body border-bottom">
          <CreateComment 
            onCommentCreated={handleNewComment}
            nodeId={nodeId}
          />
        </div>
      )}

      <div className="list-group list-group-flush">
        {comments.length === 0 ? (
          <div className="text-center py-4">
            <p className="text-muted">No comments yet</p>
            <button 
              className="btn btn-link"
              onClick={() => setShowCommentForm(true)}
              disabled={!nodeId}
            >
              Be the first to add a comment
            </button>
          </div>
        ) : (
          comments.map(comment => 
            comment && (
              <CommentItem 
                key={comment.id}
                comment={comment}
                user={user}
                processing={processing}
                onEndorse={handleEndorse}
                onReport={handleReport}
              />
            )
          )
        )}
      </div>
    </div>
  );
};

export default CommentsSection;
