// NodeDetails.js
import React from 'react';
import ScoreBar from '../../utils/visuals/ScoreBar';
import '../nonGlobalStyle/NodeDetails.css';

const NodeDetails = ({ node, user}) => {
  const handleReaction = async (reactionType) => {
    if (!user?.uuid) return;
    const apiEndpoint = reactionType === 'like' 
      ? `/api/nodes/${node.id}/like`
      : `/api/nodes/${node.id}/dislike`;

    const removalEndpoint = reactionType === 'like' 
      ? `/api/nodes/${node.id}/unlike`
      : `/api/nodes/${node.id}/undislike`;

    const url = user.reaction === reactionType 
      ? `http://localhost:5000${removalEndpoint}/${user.uuid}`
      : `http://localhost:5000${apiEndpoint}/${user.uuid}`;

    try {
      const response = await fetch(url, {
        method: 'POST',
        credentials: 'include',
      });

      if (response.ok) {
        onReactionUpdate(); // Trigger data refetch
      }
    } catch (error) {
      console.error('Reaction error:', error);
    }
  };
  <div className="node-detail mb-4">
    <div className="card-body">
      <h1 className="card-title text-accent">
        {node.title || `Node ${node.id}`}: 
        {node.statementTrueFalseFlag === null
          ? " Current statement to Prove" 
          : node.statementTrueFalseFlag 
            ? " True" 
            : " False"
        }
      </h1>
      <p className="lead text-secondary">Explanation: {node.content}</p>
      <p className="lead text-secondary">Popularity: {node.popularity}</p>
      <p className="lead text-secondary">You are: {user?.username || 'Guest'}</p>

      <ScoreBar 
        score={node.popularity} 
        height={10}
        color="#f0ac29"
        label="Popularity"
      />

      <ScoreBar 
        score={node.score} 
        height={10}
        color="#4285F4"
        label="Weighted Average Score"
      />
    </div>
  </div>
};

export default NodeDetails;
