import React from 'react';
import { useNavigate } from 'react-router-dom';

const TreeNode = ({ node, depth = 0, maxDepth = 4 }) => {
  const navigate = useNavigate();

  // Terminate at exact maxDepth instead of >= maxDepth
  if (depth >= maxDepth) return null;

  return (
    <div className="tree-node" style={{ marginLeft: `${depth * 20}px` }}>
      <div 
        className="node-content p-2 mb-2 border rounded bg-light"
        onClick={() => navigate(`/nodes/${node.id}`)}
      >
        <div className="d-flex justify-content-between align-items-center">
          <span className="fw-bold">{node.name}</span>
          {node.children?.length > 0 && (
            <span className="badge bg-secondary">
              {node.children.length} child{node.children.length !== 1 ? 'ren' : ''}
            </span>
          )}
        </div>
      </div>

      {node.children && node.children.length > 0 && (
        <div className="node-children">
          {node.children.map(child => (
            <TreeNode 
              key={child.id} 
              node={child} 
              depth={depth + 1} 
              maxDepth={maxDepth} 
            />
          ))}
        </div>
      )}
    </div>
  );
};

const TreeVisualization = ({ treeData, maxDepth = 4 }) => {
  return (
    <div className="tree-visualization my-4">
      <h4 className="mb-3">Hierarchy Tree</h4>
      <div className="tree-container bg-white p-3 rounded shadow-sm">
        {treeData ? (
          <TreeNode 
            node={treeData} 
            depth={0} 
            maxDepth={maxDepth} 
          />
        ) : (
          <div className="text-center py-3">Loading tree...</div>
        )}
      </div>
    </div>
  );
};
