import React from 'react';
import {useParams, Link } from 'react-router-dom';
import CreateTopic from '../components/CreateTopic';

const RootsPage = () => {
    const { id } = useParams();

  const [roots, setRoots] = React.useState([]); // Initialize as empty array
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    const fetchRoots = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/roots/${id}`);
        const data = await response.json();

        // 1. Ensure data is always an array
        if (Array.isArray(data)) {
          setRoots(data);
        } else {
          console.error('API response is not an array:', data);
          setRoots([]); // Fallback to empty array
        }

      } catch (error) {
        console.error('Error fetching root nodes:', error);
        setRoots([]); // Fallback to empty array on error
      } finally {
        setLoading(false);
      }
    };

    fetchRoots();
  }, [id]);

  // 2. Add safeguard before mapping
  return (
    <div>
      <h1>Root Nodes</h1>
      <ul className="list-group">
        {Array.isArray(roots) ? (
          roots.map(root => (
            <li key={root.id} className="list-group-item">
              <Link to={`/node/${root.id}`}>
                Node {root.id} (Popularity: {root.popularity})
              </Link>
            </li>
          ))
        ) : (
          <li className="list-group-item">No root nodes found</li>
        )}
      </ul>
      <button className="btn btn-primary me-2" onClick={CreateTopic}>
          Create Root Node
      </button>
    </div>
  );
};

export default RootsPage;
