// NodeDetails.js
import React from 'react';
import ScoreBar from '../../utils/visuals/ScoreBar';
import '../nonGlobalStyle/NodeDetails.css';

const NodeDetails = ({ node, user }) => {
  const handleReaction = async (reactionType, isRemoving = false) => {
    if (!user?.username) {
      alert('Please log in to react to nodes');
      return;
    }

    try {
      const endpoint = isRemoving 
        ? reactionType === 1 
          ? `unlike` 
          : `undislike`
        : reactionType === 1 
          ? `like` 
          : `dislike`;

      const response = await fetch(
        `http://localhost:5000/api/nodes/${node.id}/${endpoint}/${user.id}`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      if (!response.ok) {
        throw new Error('Failed to update reaction');
      }

      // Refresh the node data after successful reaction
      window.location.reload();
    } catch (error) {
      console.error('Error updating reaction:', error);
      alert('Failed to update reaction. Please try again.');
    }
  };

  const handleLike = () => {
    if (node.userReaction === 1) {
      handleReaction(1, true); // Unlike
    } else {
      handleReaction(1); // Like
    }
  };

  const handleDislike = () => {
    if (node.userReaction === 2) {
      handleReaction(2, true); // Undislike
    } else {
      handleReaction(2); // Dislike
    }
  };

  return (
    <div className="node-detail mb-4">
      <div className="card-body">
        <h1 className="card-title text-accent">
          {node.title || `Node ${node.id}`}: 
          {node.statementTrueFalseFlag === null
            ? " Current statement to Prove" 
            : node.statementTrueFalseFlag 
              ? " True" 
              : " False"
          }
        </h1>
        <p className="lead text-secondary">Explanation: {node.content}</p>
        <p className="lead text-secondary">Popularity: {node.popularity}</p>
        <p className="lead text-secondary">You are: {user?.username || 'Guest'}</p>

        {/* Reaction buttons */}
        <div className="reaction-buttons mt-3">
          <button 
            onClick={handleLike}
            className={`btn btn-outline-primary me-2 ${node.userReaction === 1 ? 'active' : ''}`}
            disabled={!user?.username}
          >
            üëç Like ({node.likes || 0})
          </button>
          <button 
            onClick={handleDislike}
            className={`btn btn-outline-danger ${node.userReaction === 2 ? 'active' : ''}`}
            disabled={!user?.username}
          >
            üëé Dislike ({node.dislikes || 0})
          </button>
        </div>

        <ScoreBar 
          score={node.popularity} 
          height={10}
          color="#f0ac29"
          label="Popularity"
        />

        <ScoreBar 
          score={node.score} 
          height={10}
          color="#4285F4"
          label="Weighted Average Score"
        />
      </div>
    </div>
  );
};

export default NodeDetails;
