// TreeVisualization.js
import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';

// SVG Icons
const ChevronDown = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <path d="M6 9l6 6 6-6" />
  </svg>
);

const ChevronRight = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <path d="M9 18l6-6-6-6" />
  </svg>
);

const TreeNode = ({ node, depth = 0, maxDepth = 4, expandedNodes, toggleNode }) => {
  const navigate = useNavigate();

  if (!node) return null;

  const hasChildren = node.children && node.children.length > 0;
  const isExpanded = expandedNodes.has(node.id);
  const isLeaf = depth === maxDepth - 1;

  return (
    <div className="tree-node" style={{ marginLeft: `${depth * 4}px` }}>
      <div className="node-content flex items-center my-1">
        {hasChildren ? (
          <button 
            className="flex items-center justify-center w-6 h-6 rounded-full hover:bg-gray-200 transition-colors"
            onClick={() => toggleNode(node.id)}
            aria-label={isExpanded ? "Collapse" : "Expand"}
          >
            {isExpanded ? <ChevronDown /> : <ChevronRight />}
          </button>
        ) : (
          <div className="w-6 h-6 flex items-center justify-center">
            {isLeaf ? '•' : ''}
          </div>
        )}
        <div 
          className={`flex-grow p-3 ml-1 rounded-lg cursor-pointer transition-colors hover:bg-blue-50 border
                      ${isLeaf ? 'bg-green-50 border-green-200' : 'bg-white border-gray-200'}`}
          onClick={() => navigate(`/nodes/${node.id}`)}
        >
          <div className="flex justify-between items-center">
            <span className="font-medium">{node.name}</span>
            {hasChildren && !isLeaf && (
              <span className="bg-gray-100 text-gray-800 text-xs font-medium px-2 py-0.5 rounded-full">
                {node.children.length}
              </span>
            )}
          </div>
        </div>
      </div>

      {hasChildren && isExpanded && (
        <div className="node-children ml-7 pl-1 border-l border-gray-200">
          {node.children.map(child => (
            <TreeNode 
              key={child.id} 
              node={child} 
              depth={depth + 1} 
              maxDepth={maxDepth} 
              expandedNodes={expandedNodes}
              toggleNode={toggleNode}
            />
          ))}
        </div>
      )}
    </div>
  );
};

const TreeVisualization = ({ treeData, maxDepth = 4 }) => {
  const [expandedNodes, setExpandedNodes] = useState(new Set());
  const [isAllExpanded, setIsAllExpanded] = useState(false);

  const toggleNode = useCallback((nodeId) => {
    setExpandedNodes(prev => {
      const next = new Set(prev);
      if (next.has(nodeId)) {
        next.delete(nodeId);
      } else {
        next.add(nodeId);
      }
      return next;
    });
  }, []);

  const toggleExpandAll = () => {
    if (!treeData) return;

    if (isAllExpanded) {
      setExpandedNodes(new Set());
    } else {
      const getAllIds = (node, ids = []) => {
        if (node.children && node.children.length > 0) {
          ids.push(node.id);
          node.children.forEach(child => getAllIds(child, ids));
        }
        return ids;
      };
      setExpandedNodes(new Set(getAllIds(treeData)));
    }
    setIsAllExpanded(!isAllExpanded);
  };

  return (
    <div className="tree-visualization my-6">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold text-gray-800">Hierarchy Tree</h3>
        <button 
          className={`px-3 py-1.5 text-sm rounded-md transition-colors ${
            isAllExpanded 
              ? 'bg-gray-200 text-gray-800 hover:bg-gray-300' 
              : 'bg-blue-100 text-blue-700 hover:bg-blue-200'
          }`}
          onClick={toggleExpandAll}
        >
          {isAllExpanded ? 'Collapse All' : 'Expand All'}
        </button>
      </div>

      <div className="tree-container bg-white p-4 rounded-xl border border-gray-200 shadow-sm">
        {treeData ? (
          <TreeNode 
            node={treeData} 
            depth={0} 
            maxDepth={maxDepth} 
            expandedNodes={expandedNodes}
            toggleNode={toggleNode}
          />
        ) : (
          <div className="text-center py-8 text-gray-500">
            <div className="flex justify-center">
              <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
            </div>
            <p className="mt-3">Loading tree data...</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default TreeVisualization;
