import React from 'react';
import PropTypes from 'prop-types';

const ScoreBar = ({ 
  score, 
  max = 100, 
  height = 12,
  color = "#4285F4",
  label 
}) => {
  const percentage = round((Math.max(0, Math.min(max, score || 0)) / max * 100), 2);

  return (
    <div className="score-bar-container">
      {label && <div className="score-label">{label}</div>}
      <div 
        className="score-bar-background" 
        style={{ height, backgroundColor: '#eee' }}
      >
        <div 
          className="score-bar-fill"
          style={{ 
            width: `${percentage}%`,
            height: '100%',
            backgroundColor: color,
            transition: 'width 0.3s ease'
          }}
        />
      </div>
      <div className="score-value">
        {score !== null && score !== undefined ? `${score}/${max}` : 'N/A'}
      </div>
    </div>
  );
};

ScoreBar.propTypes = {
  score: PropTypes.number,
  max: PropTypes.number,
  height: PropTypes.number,
  color: PropTypes.string,
  label: PropTypes.string
};

export default ScoreBar;  // Default export
